mapper.select:
SELECT	STAT_CNT
FROM 	STATS
WHERE 	CATEGORY_ID = #{categoryId}
AND 	STAT_DATE = #{statDate}

mapper.insert:
 INSERT INTO VISITOR_LOG (
 	IP_ADDRESS, 
 	VISIT_TIME, 
 	USER_ID
	)
 VALUES (
 	#{ipAddress}, 
 	NOW(), 
 	#{userId}
	)

mapper.select:
SELECT 	COUNT(*)
FROM 	VISITOR_LOG
WHERE 	USER_ID = #{userId}

